import React, { useEffect, useState } from "react";
import {
  Button, Box, Typography, AppBar, Tabs, Tab, OutlinedInput
} from "@material-ui/core";
import { DataGrid } from "@material-ui/data-grid";
import Attachments from "./Attachments";
import Workflow from "./workflow";
import Comments from "./Comments";
import { getReimbursementItems, getTableCount } from "api";

const ObjectPage = ({ id, onBack, fromCreate }) => {
  const [activeTab, setActiveTab] = useState(0);
  const [reimItems, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [rowCount, setRowCount] = useState(0);
  const [isEditing, setIsEditing] = useState(false);

  const handleEditToggle = () => setIsEditing(!isEditing);

  const handleSave = () => {
    console.log("Saving changes...");
    setIsEditing(false);
  };

  const handleDiscardDraft = () => {
    console.log("Discarding the draft...");
    onBack();
  };

  const handleTabChange = (event, newValue) => setActiveTab(newValue);

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      const count = await getTableCount();
      setRowCount(count);
      const items = await getReimbursementItems({ $top: 15 });
      setItems(items);
      setLoading(false);
    };
    loadData();
  }, []);

  return (
    <Box padding={2} height="100vh" display="flex" flexDirection="column">
      <Box display="flex" justifyContent="space-between" alignItems="center">
        <Button variant="outlined" color="primary" onClick={onBack}>Back</Button>
        <Typography variant="h5">{id}</Typography>
        <Button
          variant="contained"
          color="secondary"
          onClick={handleEditToggle}
        >
          {isEditing ? "Cancel" : "Edit"}
        </Button>
      </Box>

      {/* Tabs and Tab Content */}
      <Box flexGrow={1} overflow="auto" paddingBottom="80px">
        <AppBar position="static" color="default">
          <Tabs
            value={activeTab}
            onChange={handleTabChange}
            variant="scrollable"
          >
            <Tab label="General Information" />
            <Tab label="Reimbursement Details" />
            <Tab label="Attachments" />
            <Tab label="Workflow History" />
            <Tab label="Comments" />
          </Tabs>
        </AppBar>

        {/* Tab panels here */}

      </Box>

      {/* Footer Buttons */}
      <Box
        position="fixed"
        bottom={0}
        left={0}
        width="100%"
        display="flex"
        justifyContent="flex-end"
        bgcolor="#f0f0f0"
        padding={2}
      >
        {isEditing ? (
          <>
            <Button variant="contained" color="primary" onClick={handleSave}>
              Save
            </Button>
            <Button variant="outlined" color="secondary" onClick={handleDiscardDraft}>
              Discard Draft
            </Button>
          </>
        ) : (
          fromCreate && (
            <>
              <Button variant="contained" color="primary">
                Create
              </Button>
              <Button variant="outlined" color="secondary" onClick={handleDiscardDraft}>
                Discard Draft
              </Button>
            </>
          )
        )}
      </Box>
    </Box>
  );
};

export default ObjectPage;
