import React, { useEffect, useState } from "react";
import { Container, Box, Toolbar, Button, Typography, TextField } from "@material-ui/core";
import { DataGrid } from "@material-ui/data-grid";
import { getTableData, getTableCount } from "api";

const columns = [
  { field: "reimbursmentId", headerName: "Reimbursment ID", width: 250 },
  { field: "reimbursementDate", headerName: "Reimbursement Date", flex: 1 },
  { field: "totalAmount", headerName: "Total Amount", flex: 1 },
];

const PAGE_SIZE = 15;

export default function MasterPage() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [rowCount, setRowCount] = useState(0);

  // Filter state
  const [filterValues, setFilterValues] = useState({
    reimbursmentId: '',
    reimbursementDate: '',
    totalAmount: ''
  });

  const loadData = async (isFirstLoad, skip = 0, filters = {}) => {
    try {
      setItems([]);
      setLoading(true);

      if (isFirstLoad) {
        const count = await getTableCount();
        setRowCount(count);
      }

      const _items = await getTableData({
        $top: PAGE_SIZE,
        $skip: skip,
        ...filters // Add filters to the request
      });
      const itemsWithIds = _items.map((item, index) => {
        item.id = index + skip; // Ensure unique IDs across pages
        return item;
      });
      setItems(itemsWithIds);
    } finally {
      setLoading(false);
    }
  };

  const handlePageChanged = ({ page }) => {
    loadData(false, page * PAGE_SIZE, filterValues); // Pass current filters
  };

  const handleAdd = () => {
    console.log("Add button clicked");
  };

  const handleRefresh = () => {
    loadData(true, 0, filterValues); // Reload the first page with current filters
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilterValues({
      ...filterValues,
      [name]: value,
    });
  };

  const handleApplyFilter = () => {
    loadData(true, 0, filterValues); // Reload the first page with new filters
  };

  useEffect(() => {
    loadData(true);
  }, []);

  return (
    <Container disableGutters>
      <Box mb={2}>
        <Toolbar>
        </Toolbar>
        {/* Filter Bar */}
        <Toolbar>
          <TextField
            label="Reimbursment ID"
            variant="outlined"
            name="reimbursmentId"
            value={filterValues.reimbursmentId}
            onChange={handleFilterChange}
            style={{ marginRight: '8px' }}
          />
          <TextField
            label="Reimbursement Date"
            variant="outlined"
            name="reimbursementDate"
            type="date"
            value={filterValues.reimbursementDate}
            onChange={handleFilterChange}
            InputLabelProps={{
              shrink: true,
            }}
            style={{ marginRight: '8px' }}
          />
          <TextField
            label="Total Amount"
            variant="outlined"
            name="totalAmount"
            value={filterValues.totalAmount}
            onChange={handleFilterChange}
            style={{ marginRight: '8px' }}
          />
          <Button variant="contained" color="primary" onClick={handleApplyFilter}>
            Apply Filters
          </Button>
        </Toolbar>
      </Box>
      <Toolbar>
        <Typography variant="h6" style={{ flex: 1 }}>
          Reimbursement Records
        </Typography>
        <Button variant="contained" color="primary" onClick={handleAdd}>
          Create
        </Button>
        <Button variant="outlined" color="secondary" onClick={handleRefresh} style={{ marginLeft: '8px' }}>
          Refresh
        </Button>
      </Toolbar>
      <Box height="80vh" py={5}>
        <DataGrid
          loading={loading}
          rows={items}
          columns={columns}
          pageSize={PAGE_SIZE}
          paginationMode="server"
          rowCount={rowCount}
          onPageChange={handlePageChanged}
        />
      </Box>
    </Container>
  );
}
