import React, { useState, useCallback } from "react";
import { Button, Box, Typography, Paper } from "@mui/material";
import { debounce } from "lodash";

const Attachments = () => {
  const [files, setFiles] = useState([]);

  const handleFileUpload = useCallback(
    debounce((event) => {
      const file = event.target.files[0];
      if (file) {
        setFiles((prevFiles) => [
          ...prevFiles,
          {
            name: file.name,
            size: file.size,
            type: file.type,
            lastModified: file.lastModified,
          },
        ]);
      }
    }, 300),
    []
  );

  const getFileIcon = (fileType) => {
    // Return a simple text-based representation of the file type
    if (fileType.includes("pdf")) return "üìÑ"; // PDF icon
    if (fileType.includes("word") || fileType.includes("doc")) return "üìù"; // Word icon
    if (fileType.includes("image")) return "üñºÔ∏è"; // Image icon
    return "üìÅ"; // Default file icon
  };

  return (
    <Box>
      {/* File Upload Input */}
      <input
        accept="*/*"
        style={{ display: "none" }}
        id="file-upload"
        type="file"
        onChange={handleFileUpload}
      />
      <label htmlFor="file-upload" style={{ marginLeft: "92%" }}>
        <Button variant="contained" color="primary" component="span">
          Upload File
        </Button>
      </label>

      {/* Display Uploaded Files Information */}
      {files.length > 0 && (
        <Paper style={{ marginTop: "16px", padding: "16px" }}>
          <Typography variant="h6" style={{ marginBottom: "16px" }}>
            Uploaded Files
          </Typography>
          {files.map((file, index) => (
            <Box key={index} mb={2} display="flex" alignItems="center">
              {/* Display File Icon */}
              <Box mr={2} style={{ fontSize: "24px" }}>
                {getFileIcon(file.type)}
              </Box>

              {/* File Details */}
              <Typography variant="body1" style={{ marginRight: "8px" }}>
                <strong>File Name:</strong>
              </Typography>
              <Typography variant="body1">{file.name}</Typography>

              <Typography variant="body1" style={{ marginRight: "8px" }}>
                <strong>File Size:</strong>
              </Typography>
              <Typography variant="body1">{`${file.size} bytes`}</Typography>

              <Typography variant="body1" style={{ marginRight: "8px" }}>
                <strong>File Type:</strong>
              </Typography>
              <Typography variant="body1">{file.type}</Typography>

              <Typography variant="body1" style={{ marginRight: "8px" }}>
                <strong>Last Modified:</strong>
              </Typography>
              <Typography variant="body1">
                {new Date(file.lastModified).toLocaleDateString()}
              </Typography>
            </Box>
          ))}
        </Paper>
      )}
    </Box>
  );
};

export default Attachments;
