import React, { useState } from "react";
import { Button, Box, Typography, Table, TableHead, TableBody, TableRow, TableCell, Paper } from "@material-ui/core";

const Attachments = () => {
  const [files, setFiles] = useState([]);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Add the new file to the files array
      setFiles([...files, { name: file.name, size: file.size, type: file.type }]);
    }
  };

  return (
    <Box>
      {/* File Upload Input */}
      <input
        accept="*/*"
        style={{ display: "none" }}
        id="file-upload"
        type="file"
        onChange={handleFileUpload}
      />
      <label htmlFor="file-upload" style={{marginLeft:"92%"}}>
        <Button variant="contained" color="primary" component="span">
          Upload File
        </Button>
      </label>

      {files.length > 0 && (
        <Paper style={{ marginTop: "16px", padding: "16px" }}>
          <Typography variant="h6" style={{ marginBottom: "16px" }}>
            Uploaded Files
          </Typography>

          {files.map((file, index) => (
            <Box key={index} mb={2} display="flex" flexDirection="column">
              <Box display="flex" alignItems="center" mb={1}>
                <Typography variant="body1" style={{ width: "150px" }}><strong>File Name:</strong></Typography>
                <OutlinedInput
                  value={file.name}
                  fullWidth
                  readOnly
                  style={{ marginLeft: "16px", backgroundColor: "#f5f5f5" }}
                />
              </Box>
              <Box display="flex" alignItems="center" mb={1}>
                <Typography variant="body1" style={{ width: "150px" }}><strong>File Size:</strong></Typography>
                <OutlinedInput
                  value={`${file.size} bytes`}
                  fullWidth
                  readOnly
                  style={{ marginLeft: "16px", backgroundColor: "#f5f5f5" }}
                />
              </Box>
              <Box display="flex" alignItems="center">
                <Typography variant="body1" style={{ width: "150px" }}><strong>File Type:</strong></Typography>
                <OutlinedInput
                  value={file.type}
                  fullWidth
                  readOnly
                  style={{ marginLeft: "16px", backgroundColor: "#f5f5f5" }}
                />
              </Box>
            </Box>
          ))}
        </Paper>
      )}
    </Box>
  );
};

export default Attachments;
