import React, { useState } from "react";
import { Button, Box, Typography } from "@material-ui/core";

const ObjectPage = ({ id, onBack }) => {
  const [activeSection, setActiveSection] = useState("general"); // Default active section

  const handleSectionChange = (section) => {
    setActiveSection(section); // Set the active section when a button is clicked
  };

  return (
    <Box p={3}>
      <Typography variant="h4" style={{ marginBottom: "16px" }}>
        Reimbursement ID: {id}
      </Typography>

      <Box display="flex" mb={2}>
        <Button
          variant={activeSection === "general" ? "contained" : "outlined"}
          color={activeSection === "general" ? "primary" : "default"}
          onClick={() => handleSectionChange("general")}
          style={{ marginRight: "8px" }}
        >
          General Information
        </Button>
        <Button
          variant={activeSection === "details" ? "contained" : "outlined"}
          color={activeSection === "details" ? "primary" : "default"}
          onClick={() => handleSectionChange("details")}
          style={{ marginRight: "8px" }}
        >
          Reimbursement Details
        </Button>
        <Button
          variant={activeSection === "attachments" ? "contained" : "outlined"}
          color={activeSection === "attachments" ? "primary" : "default"}
          onClick={() => handleSectionChange("attachments")}
          style={{ marginRight: "8px" }}
        >
          Attachments
        </Button>
        <Button
          variant={activeSection === "workflow" ? "contained" : "outlined"}
          color={activeSection === "workflow" ? "primary" : "default"}
          onClick={() => handleSectionChange("workflow")}
          style={{ marginRight: "8px" }}
        >
          Workflow History
        </Button>
        <Button
          variant={activeSection === "comments" ? "contained" : "outlined"}
          color={activeSection === "comments" ? "primary" : "default"}
          onClick={() => handleSectionChange("comments")}
        >
          Comments
        </Button>
      </Box>

      {/* Subsection Content */}
      <Box mt={3}>
        {activeSection === "general" && (
          <Typography variant="h6">General Information Content</Typography>
        )}
        {activeSection === "details" && (
          <Typography variant="h6">Reimbursement Details Content</Typography>
        )}
        {activeSection === "attachments" && (
          <Typography variant="h6">Attachments Content</Typography>
        )}
        {activeSection === "workflow" && (
          <Typography variant="h6">Workflow History Content</Typography>
        )}
        {activeSection === "comments" && (
          <Typography variant="h6">Comments Content</Typography>
        )}
      </Box>

      <Button variant="contained" color="secondary" onClick={onBack} style={{ marginTop: "20px" }}>
        Back to List
      </Button>
    </Box>
  );
};

export default ObjectPage;
