import React, { useState } from "react";
import { Button, Box, Typography, OutlinedInput, Table, TableHead, TableBody, TableRow, TableCell, Paper } from "@material-ui/core";

const Attachments = () => {
  const [files, setFiles] = useState([]);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Add the new file to the files array
      setFiles([...files, { name: file.name, size: file.size, type: file.type }]);
    }
  };

  return (
    <Box>
      {/* File Upload Input */}
      <input
        accept="*/*"
        style={{ display: "none" }}
        id="file-upload"
        type="file"
        onChange={handleFileUpload}
      />
      <label htmlFor="file-upload" style={{marginLeft:"92%"}}>
        <Button variant="contained" color="primary" component="span">
          Upload File
        </Button>
      </label>

     {/* Display Uploaded Files Information */}
     {files.length > 0 && (
        <Paper style={{ marginTop: "16px", padding: "16px" }}>
          <Typography variant="h6" style={{ marginBottom: "16px" }}>
            Uploaded Files
          </Typography>

          {files.map((file, index) => (
            <Box key={index} mb={2} display="flex" flexDirection="row" alignItems="center">
              <Box display="flex" alignItems="center" mr={4}>
                <Typography variant="body1" style={{ marginRight: "8px" }}><strong>File Name:</strong></Typography>
                <Typography variant="body1">{file.name}</Typography>
              </Box>
              <Box display="flex" alignItems="center" mr={4}>
                <Typography variant="body1" style={{ marginRight: "8px" }}><strong>File Size:</strong></Typography>
                <Typography variant="body1">{`${file.size} bytes`}</Typography>
              </Box>
              <Box display="flex" alignItems="center">
                <Typography variant="body1" style={{ marginRight: "8px" }}><strong>File Type:</strong></Typography>
                <OutlinedInput
                  value={file.type}
                  readOnly
                  style={{ width: "150px", backgroundColor: "#f5f5f5" }}
                />
              </Box>
            </Box>
          ))}
        </Paper>
      )}
    </Box>
  );
};

export default Attachments;
