import React, { useState } from "react";
import { Button, Box, Typography, AppBar, Tabs, Tab } from "@material-ui/core";
import { getReimbursementItems } from "api";


const ObjectPage = async({ id, onBack }) => {
  const [activeTab, setActiveTab] = useState(0); // State to track active tab

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue); // Update active tab based on user selection
  };

  //reimbursement item objpage table
  const PAGE_SIZE = 15;

  const loadData = async(skip=0)=>{
    try{
      const reimItems = await getReimbursementItems({
        $top: PAGE_SIZE,
        $skip: skip
      });
    }finally {
      setLoading(false);
    }

  };
  useEffect(() => {
    loadData(true);
  }, []);









  return (
    <div style={{ padding: "16px" }}>
      {/* Back button and Reimbursement ID */}
      <Button variant="outlined" color="primary" onClick={onBack} style={{marginRight:"93%"}}>
        Back to List
      </Button>
      <Typography variant="h5" style={{ marginTop: "16px", marginBottom: "16px", marginRight:"87%" }}>
        Reimbursement ID: {id}
      </Typography>

      {/* Tab Navigation */}
      <AppBar position="static">
        <Tabs value={activeTab} onChange={handleTabChange} variant="scrollable" scrollButtons="auto">
          <Tab label="General Information" />
          <Tab label="Reimbursement Details" />
          <Tab label="Attachments" />
          <Tab label="Workflow History" />
          <Tab label="Comments" />
        </Tabs>
      </AppBar>

      {/* Tab Content */}
      <Box padding={2} border={1} borderColor="grey.300" borderRadius="4px" marginTop="16px">
        {/* General Information */}
        <Box
          style={{
            backgroundColor: activeTab === 0 ? "#e0f7fa" : "transparent", // Highlight active tab
            padding: activeTab === 0 ? "16px" : "0", // Add padding if active
          }}
        >
          <Typography variant="h6" style={{ marginBottom: "8px", marginRight:"1440px"}}> {/* Align title to the left */}
           <strong>General Information</strong> 
          </Typography>

          <Box display="flex" flexDirection="row" alignItems="center" marginTop={2}>
              <Box marginRight={2}> {/* Space between fields */}
                <Typography variant="body1">
                  <strong>Reimbursement Date:</strong> {'11-11-2024'}
                </Typography>
              </Box>
              <Box marginRight={2}> {/* Space between fields */}
                <Typography variant="body1">
                  <strong>Total Amount:</strong> {'9000'}
                </Typography>
              </Box>
              <Box>
                <Typography variant="body1">
                  <strong>Status:</strong> {'New'}
                </Typography>
              </Box>
            </Box>
        </Box>


        {/* Reimbursement Details-=====================================================================================================-========================== */}
        <Box
          style={{
            backgroundColor: activeTab === 1 ? "#e0f7fa" : "transparent", // Highlight active tab
            padding: activeTab === 1 ? "16px" : "0", // Add padding if active
            marginTop: activeTab === 0 ? "16px" : "0", // Add margin to separate if previous section is shown
          }}
        >
          <Typography variant="h6" style={{ marginBottom: "8px", marginRight:"1440px"}}><strong>Reimbursement Details</strong></Typography>
          {activeTab === 1 && (

            <Typography variant="body1">
              {/* Content for Reimbursement Details */}
              Reimbursement details content goes here.


            </Typography>
          )}
        </Box>

        {/* Attachments */}
        <Box
          style={{
            backgroundColor: activeTab === 2 ? "#e0f7fa" : "transparent", // Highlight active tab
            padding: activeTab === 2 ? "16px" : "0", // Add padding if active
            marginTop: activeTab === 1 || activeTab === 0 ? "16px" : "0", // Add margin to separate if previous section is shown
          }}
        >
          <Typography variant="h6">Attachments</Typography>
          {activeTab === 2 && (
            <Typography variant="body1">
              {/* Content for Attachments */}
              Attachments content goes here.
            </Typography>
          )}
        </Box>

        {/* Workflow History */}
        <Box
          style={{
            backgroundColor: activeTab === 3 ? "#e0f7fa" : "transparent", // Highlight active tab
            padding: activeTab === 3 ? "16px" : "0", // Add padding if active
            marginTop: activeTab === 2 || activeTab === 1 || activeTab === 0 ? "16px" : "0", // Add margin to separate if previous section is shown
          }}
        >
          <Typography variant="h6">Workflow History</Typography>
          {activeTab === 3 && (
            <Typography variant="body1">
              {/* Content for Workflow History */}
              Workflow history content goes here.
            </Typography>
          )}
        </Box>

        {/* Comments */}
        <Box
          style={{
            backgroundColor: activeTab === 4 ? "#e0f7fa" : "transparent", // Highlight active tab
            padding: activeTab === 4 ? "16px" : "0", // Add padding if active
            marginTop: activeTab === 3 || activeTab === 2 || activeTab === 1 || activeTab === 0 ? "16px" : "0", // Add margin to separate if previous section is shown
          }}
        >
          <Typography variant="h6">Comments</Typography>
          {activeTab === 4 && (
            <Typography variant="body1">
              {/* Content for Comments */}
              Comments content goes here.
            </Typography>
          )}
        </Box>
      </Box>
    </div>
  );
};

export default ObjectPage;
