import React, { useState, useCallback } from "react";
import {
  Button,
  Box,
  Typography,
  Paper,
  IconButton,
  Avatar,
} from "@material-ui/core";
import { debounce } from "lodash";
import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from "@material-ui/icons/Edit";
import InsertDriveFileIcon from "@material-ui/icons/InsertDriveFile";
import PictureAsPdfIcon from "@material-ui/icons/PictureAsPdf";
import ImageIcon from "@material-ui/icons/Image";
import DescriptionIcon from "@material-ui/icons/Description";
import InsertChartIcon from "@material-ui/icons/InsertChart";
import VideoLibraryIcon from "@material-ui/icons/VideoLibrary";
import AudiotrackIcon from "@material-ui/icons/Audiotrack";

const getFileIcon = (fileType) => {
  if (fileType.includes("pdf")) {
    return <PictureAsPdfIcon />;
  }
  if (fileType.includes("image")) {
    return <ImageIcon />;
  }
  if (fileType.includes("word") || fileType.includes("msword")) {
    return <DescriptionIcon />;
  }
  if (fileType.includes("excel") || fileType.includes("spreadsheet")) {
    return <InsertChartIcon />;
  }
  if (fileType.includes("video")) {
    return <VideoLibraryIcon />;
  }
  if (fileType.includes("audio")) {
    return <AudiotrackIcon />;
  }
  // Default file icon
  return <InsertDriveFileIcon />;
};

const Attachments = () => {
  const [files, setFiles] = useState([]);

  const handleFileUpload = useCallback(
    debounce((event) => {
      const file = event.target.files[0];
      if (file) {
        setFiles((prevFiles) => [
          ...prevFiles,
          {
            name: file.name,
            size: file.size,
            type: file.type,
            lastModified: file.lastModified,
            lastModifiedDate: file.lastModifiedDate,
            previewUrl: URL.createObjectURL(file),
          },
        ]);
      }
    }, 300),
    []
  ); // Debounce for 300ms

  const handleRemoveFile = (index) => {
    setFiles((prevFiles) => prevFiles.filter((_, i) => i !== index));
  };

  return (
    <Box>
      {/* File Upload Input */}
      <input
        accept="*/*"
        style={{ display: "none" }}
        id="file-upload"
        type="file"
        onChange={handleFileUpload}
      />
      <label htmlFor="file-upload" style={{ marginLeft: "92%" }}>
        <Button variant="contained" color="primary" component="span">
          Upload File
        </Button>
      </label>

      {/* Display Background Image if no files */}
      {files.length === 0 && (
        <Box
          mt={4}
          height={200}
          display="flex"
          alignItems="center"
          justifyContent="center"
          style={{
            backgroundImage: "url('https://via.placeholder.com/300x200')",
            backgroundSize: "cover",
            backgroundPosition: "center",
          }}
        >
          <Typography
            variant="h6"
            style={{ color: "#fff", textShadow: "1px 1px 2px rgba(0,0,0,0.7)" }}
          >
            No files uploaded yet
          </Typography>
        </Box>
      )}

      {/* Display Uploaded Files Information */}
      {files.length > 0 && (
        <Paper style={{ marginTop: "16px", padding: "16px" }}>
          <Typography variant="h6" style={{ marginBottom: "16px" }}>
            Uploaded Files
          </Typography>
          {files.map((file, index) => (
            <Box
              key={index}
              mb={2}
              display="flex"
              alignItems="center"
              justifyContent="space-between"
              borderBottom="1px solid #ddd"
              pb={1}
            >
              {/* File Icon */}
              <Avatar style={{ backgroundColor: "#f5f5f5", marginRight: "16px" }}>
                {getFileIcon(file.type)}
              </Avatar>

              {/* File Details */}
              <Box display="flex" flexGrow={1}>
                <Typography variant="body1" style={{ marginRight: "16px" }}>
                  <strong>File Name:</strong> {file.name}
                </Typography>
                <Typography variant="body1" style={{ marginRight: "16px" }}>
                  <strong>File Size:</strong> {`${file.size} bytes`}
                </Typography>
                <Typography variant="body1" style={{ marginRight: "16px" }}>
                  <strong>File Type:</strong> {file.type}
                </Typography>
                <Typography variant="body1" style={{ marginRight: "16px" }}>
                  <strong>Last Modified:</strong> {new Date(file.lastModified).toLocaleDateString()}
                </Typography>
              </Box>

              {/* Edit and Remove Icons */}
              <Box>
                <IconButton onClick={() => console.log(`Edit ${file.name}`)}>
                  <EditIcon color="primary" />
                </IconButton>
                <IconButton onClick={() => handleRemoveFile(index)}>
                  <DeleteIcon color="secondary" />
                </IconButton>
              </Box>
            </Box>
          ))}
        </Paper>
      )}
    </Box>
  );
};

export default Attachments;
