import { Button, Box, Typography, AppBar, Tabs, Tab, OutlinedInput } from "@material-ui/core";
import { getReimbursementItems, getTableCount } from "api";
import React, { useEffect, useState, useRef } from "react";
import { DataGrid } from "@material-ui/data-grid";

const ObjectPage = ({ id, onBack }) => {
  const [activeTab, setActiveTab] = useState(0);
  const [reimItems, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [rowCount, setRowCount] = useState(0);

  const generalInfoRef = useRef(null);
  const reimbursementDetailsRef = useRef(null);
  const attachmentsRef = useRef(null);
  const workflowHistoryRef = useRef(null);
  const commentsRef = useRef(null);

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
    scrollToSection(newValue);
  };

  const scrollToSection = (tabIndex) => {
    let sectionRef;
    switch (tabIndex) {
      case 0:
        sectionRef = generalInfoRef;
        break;
      case 1:
        sectionRef = reimbursementDetailsRef;
        break;
      case 2:
        sectionRef = attachmentsRef;
        break;
      case 3:
        sectionRef = workflowHistoryRef;
        break;
      case 4:
        sectionRef = commentsRef;
        break;
      default:
        break;
    }
    if (sectionRef && sectionRef.current) {
      sectionRef.current.scrollIntoView({ behavior: "smooth", block: "start" });
    }
  };

  const columns = [
    { field: "reimbursmentId", headerName: "Reimbursment ID", width: 250 },
    { field: "item", headerName: "Item", flex: 1 },
    { field: "reimbursmentType", headerName: "Reimbursment Type", flex: 1 },
    { field: "reimbursmentDate", headerName: "Reimbursment Date", flex: 1 },
    { field: "amountToBeReimbursed", headerName: "Amount To Be Reimbursed", flex: 1 },
    { field: "amountEligibleToClaim", headerName: "Amount Eligible To Claim", flex: 1 }
  ];

  const PAGE_SIZE = 15;

  const loadData = async (isFirstLoad, skip = 0) => {
    try {
      setLoading(true);
      setItems([]);

      if (isFirstLoad) {
        const count = await getTableCount();
        setRowCount(count);
      }

      const _reimItems = await getReimbursementItems({
        $top: PAGE_SIZE,
        $skip: skip
      });
      const reimitemsWithIds = _reimItems.map((reimItem, index) => ({
        ...reimItem,
        id: index + skip
      }));
      setItems(reimitemsWithIds);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadData(true);
  }, []);

  return (
    <div style={{ padding: "16px" }}>
      <Button variant="outlined" color="primary" onClick={onBack} style={{ marginRight: "92%" }}>
        Back to List
      </Button>
      <Typography variant="h5" style={{ marginTop: "16px", marginBottom: "16px", marginRight: "95%" }}>
        {id}
      </Typography>

      {/* Scrollable area starts here */}
      <Box
       style={{
        overflowY: "auto", // Enable vertical scrolling if content overflows
        padding: "16px",
        border: "1px solid #ddd",
        borderRadius: "4px",
      }}
      >
        {/* AppBar with Tabs */}
        <AppBar position="fixed" style={{ backgroundColor: "#f0f0f0", boxShadow: "none" }}>
          <Tabs
            value={activeTab}
            onChange={handleTabChange}
            variant="scrollable"
            scrollButtons="auto"
            TabIndicatorProps={{ style: { backgroundColor: "black" } }} // Sets the indicator color to black
          >
            <Tab label="General Information" style={{ color: "black" }} />
            <Tab label="Reimbursement Details" style={{ color: "black" }} />
            <Tab label="Attachments" style={{ color: "black" }} />
            <Tab label="Workflow History" style={{ color: "black" }} />
            <Tab label="Comments" style={{ color: "black" }} />
          </Tabs>
        </AppBar>

        {/* General Information Section */}
        <Box
          ref={generalInfoRef}
          style={{
            backgroundColor: activeTab === 0 ? "#f0f0f0" : "transparent",
            padding: "16px",
            marginRight: "70%",
            marginTop: "16px", // Add margin to space out sections
          }}
        >
          <Typography variant="h6"><strong>General Information</strong></Typography>
          <Box display="flex" flexDirection="row" alignItems="center" marginTop={2}>
            <Box marginRight={2}>
              <Typography variant="body1"><strong>Reimbursement Date:</strong> {'11-11-2024'}</Typography>
            </Box>
            <Box marginRight={2}>
              <Typography variant="body1"><strong>Total Amount:</strong> {'9000'}</Typography>
            </Box>
            <Box>
              <Typography variant="body1"><strong>Status:</strong> {'New'}</Typography>
            </Box>
          </Box>
        </Box>

        {/* Reimbursement Details Section */}
        <Box
          ref={reimbursementDetailsRef}
          style={{
            backgroundColor: activeTab === 1 ? "#f0f0f0" : "transparent",
            padding: "16px",
            marginTop: "16px"
          }}
        >
          <Typography variant="h6"><strong>Reimbursement Details</strong></Typography>
          <Box py={5}>
            <DataGrid
              loading={loading}
              rows={reimItems}
              columns={columns}
              pageSize={PAGE_SIZE}
              paginationMode="server"
              rowCount={rowCount}
              autoHeight
            />
          </Box>
        </Box>

        {/* Attachments Section */}
        <Box
          ref={attachmentsRef}
          style={{
            backgroundColor: activeTab === 2 ? "#f0f0f0" : "transparent",
            padding: "16px",
            marginTop: "16px"
          }}
        >
          <Typography variant="h6"><strong>Attachments</strong></Typography>
          <Typography variant="body1">Attachments content goes here.</Typography>
        </Box>

        {/* Workflow History Section */}
        <Box
          ref={workflowHistoryRef}
          style={{
            backgroundColor: activeTab === 3 ? "#f0f0f0" : "transparent",
            padding: "16px",
            marginTop: "16px"
          }}
        >
          <Typography variant="h6"><strong>Workflow History</strong></Typography>
          <Typography variant="body1">Workflow history content goes here.</Typography>
        </Box>

        {/* Comments Section */}
        <Box
          ref={commentsRef}
          style={{
            backgroundColor: activeTab === 4 ? "#f0f0f0" : "transparent",
            padding: "16px",
            marginTop: "16px"
          }}
        >
          <Typography variant="h6"><strong>Comments</strong></Typography>
          <Button
            variant="contained"
            color="primary"
            style={{ marginBottom: "4px" }}
            onClick={() => console.log("Comment History clicked")}
          >
            Comment History
          </Button>
          <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
            <Box flexGrow={1} mr={2}>
              <OutlinedInput
                placeholder="Add a comment..."
                multiline
                rows={3}
                fullWidth
                variant="outlined"
              />
            </Box>
          </Box>
        </Box>
      </Box>
    </div>
  );
};

export default ObjectPage;
