import React, { useState } from "react";
import { Container, Button, Typography, Box } from "@material-ui/core";

const ObjectPage = ({ id, onBack }) => {
  const [activeSection, setActiveSection] = useState("general"); // Default active section

  const handleSectionChange = (section) => {
    setActiveSection(section); // Set the active section
  };

  return (
    <Container style={{ padding: "20px" }}>
      <Typography variant="h6" style={{ marginBottom: "20px" }}>
        Reimbursement ID: {id}
      </Typography>

      {/* Subsection Navigation */}
      <Box display="flex" mb={2}>
        {["general", "details", "attachments", "workflow", "comments"].map((section) => (
          <Button
            key={section}
            variant={activeSection === section ? "contained" : "outlined"}
            color={activeSection === section ? "primary" : "default"}
            onClick={() => handleSectionChange(section)}
            style={{ marginRight: "10px" }}
          >
            {section.charAt(0).toUpperCase() + section.slice(1).replace(/([a-z])([A-Z])/g, '$1 $2')}
          </Button>
        ))}
      </Box>

      {/* Section Content */}
      <Box>
        {activeSection === "general" && (
          <Box>
            <Typography variant="h6">General Information</Typography>
            {/* Fields for General Information */}
            <Typography>Name: John Doe</Typography>
            <Typography>Department: Finance</Typography>
            {/* Add more fields as needed */}
          </Box>
        )}
        {activeSection === "details" && (
          <Box>
            <Typography variant="h6">Reimbursement Details</Typography>
            {/* Fields for Reimbursement Details */}
            <Typography>Amount: $100.00</Typography>
            <Typography>Date: 2024-10-22</Typography>
            {/* Add more fields as needed */}
          </Box>
        )}
        {activeSection === "attachments" && (
          <Box>
            <Typography variant="h6">Attachments</Typography>
            {/* Fields for Attachments */}
            <Typography>No attachments available.</Typography>
          </Box>
        )}
        {activeSection === "workflow" && (
          <Box>
            <Typography variant="h6">Workflow History</Typography>
            {/* Fields for Workflow History */}
            <Typography>Status: Approved</Typography>
            <Typography>Approved By: Jane Smith</Typography>
            {/* Add more fields as needed */}
          </Box>
        )}
        {activeSection === "comments" && (
          <Box>
            <Typography variant="h6">Comments</Typography>
            {/* Fields for Comments */}
            <Typography>No comments yet.</Typography>
          </Box>
        )}
      </Box>

      {/* Back Button */}
      <Button variant="contained" color="secondary" onClick={onBack} style={{ marginTop: "20px" }}>
        Back to List
      </Button>
    </Container>
  );
};

export default ObjectPage;
