import React, { useState } from "react";
import { Button, Box, Typography, Paper } from "@material-ui/core";
import { FileIcon, defaultStyles } from 'react-file-icon';
import PictureAsPdfIcon from '@mui/icons-material/PictureAsPdf';
import DescriptionIcon from '@mui/icons-material/Description';
import ImageIcon from '@mui/icons-material/Image';
import InsertDriveFileIcon from '@mui/icons-material/InsertDriveFile';

const Attachments = () => {
  const [files, setFiles] = useState([]);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setFiles([...files, { name: file.name, size: file.size, type: file.type, lastModified: file.lastModified, lastModifiedDate: file.lastModifiedDate }]);
    }
  };

  const getFileIcon = (fileType) => {
    if (fileType.includes("pdf")) return <PictureAsPdfIcon />;
    if (fileType.includes("word") || fileType.includes("doc")) return <DescriptionIcon />;
    if (fileType.includes("image")) return <ImageIcon />;
    return <InsertDriveFileIcon />; // Default file icon
  };

  return (
    <Box>
      {/* File Upload Input */}
      <input
        accept="*/*"
        style={{ display: "none" }}
        id="file-upload"
        type="file"
        onChange={handleFileUpload}
      />
      <label htmlFor="file-upload" style={{marginLeft:"92%"}}>
        <Button variant="contained" color="primary" component="span">
          Upload File
        </Button>
      </label>

      {/* Display Uploaded Files Information */}
      {files.length > 0 && (
        <Paper style={{ marginTop: "16px", padding: "16px" }}>
          <Typography variant="h6" style={{ marginBottom: "16px" }}>
            Uploaded Files
          </Typography>

          {files.map((file, index) => (
            <Box key={index} mb={2} display="flex" alignItems="center">
              {/* Display File Icon */}
              <Box mr={2}>
                {getFileIcon(file.type)}
              </Box>

              {/* File Details */}
              <Box display="flex" alignItems="center" mr={4}>
                <Typography variant="body1" style={{ marginRight: "8px" }}><strong>File Name:</strong></Typography>
                <Typography variant="body1">{file.name}</Typography>
              </Box>

              <Box display="flex" alignItems="center" mr={4}>
                <Typography variant="body1" style={{ marginRight: "8px" }}><strong>File Size:</strong></Typography>
                <Typography variant="body1">{`${file.size} bytes`}</Typography>
              </Box>

              <Box display="flex" alignItems="center" mr={4}>
                <Typography variant="body1" style={{ marginRight: "8px" }}><strong>File Type:</strong></Typography>
                <Typography variant="body1">{file.type}</Typography>
              </Box>

              <Box display="flex" alignItems="center" mr={4}>
                <Typography variant="body1" style={{ marginRight: "8px" }}><strong>Last Modified:</strong></Typography>
                <Typography variant="body1">{new Date(file.lastModified).toLocaleDateString()}</Typography>
              </Box>

              <Box display="flex" alignItems="center">
                <Typography variant="body1" style={{ marginRight: "8px" }}><strong>Last Modified Date:</strong></Typography>
                <Typography variant="body1">{file.lastModifiedDate.toLocaleDateString()}</Typography>
              </Box>
            </Box>
          ))}
        </Paper>
      )}
    </Box>
  );
};

export default Attachments;
