import React, { useState } from "react";
import { Button, Box, Typography, Paper } from "@material-ui/core";

const ObjectPage = ({ id, onBack }) => {
  // State to track the currently selected subsection
  const [activeSection, setActiveSection] = useState("general"); // Default to "General Information"

  // Function to handle subsection clicks
  const handleSectionClick = (section) => {
    setActiveSection(section);
  };

  // Define the sections
  const sections = [
    { key: "general", label: "General Information" },
    { key: "reimbursement", label: "Reimbursement Details" },
    { key: "attachments", label: "Attachments" },
    { key: "workflow", label: "Workflow History" },
    { key: "comments", label: "Comments" },
  ];

  return (
    <Box p={3}>
      {/* Header with the ID */}
      <Typography variant="h4" gutterBottom>
        Reimbursement ID: {id}
      </Typography>

      {/* Subsection Navigation */}
      <Box mb={2}>
        {sections.map((section) => (
          <Button
            key={section.key}
            variant="outlined"
            onClick={() => handleSectionClick(section.key)}
            style={{
              marginRight: "8px",
              backgroundColor: activeSection === section.key ? "#e0e0e0" : "transparent", // Highlight selected section
              fontWeight: activeSection === section.key ? "bold" : "normal",
            }}
          >
            {section.label}
          </Button>
        ))}
      </Box>

      {/* Display Content Based on Active Section */}
      <Paper elevation={3} style={{ padding: "16px" }}>
        <Typography variant="h6">{sections.find(sec => sec.key === activeSection).label}</Typography>
        <Typography variant="body1">
          {/* Content for each section can be expanded based on requirements */}
          {activeSection === "general" && "General information content goes here."}
          {activeSection === "reimbursement" && "Reimbursement details content goes here."}
          {activeSection === "attachments" && "Attachments content goes here."}
          {activeSection === "workflow" && "Workflow history content goes here."}
          {activeSection === "comments" && "Comments content goes here."}
        </Typography>
      </Paper>

      {/* Back button */}
      <Button variant="contained" color="secondary" onClick={onBack} style={{ marginTop: "16px" }}>
        Back to List
      </Button>
    </Box>
  );
};

export default ObjectPage;
