import React, { useState } from "react";
import { Button, Box, Typography, Paper } from "@mui/material";

const Attachments = () => {
  const [files, setFiles] = useState([]);

  const handleFileUpload = (event) => {
    const uploadedFiles = Array.from(event.target.files);
    const newFiles = uploadedFiles.map((file) => ({
      name: file.name,
      size: file.size,
      type: file.type,
      lastModified: file.lastModified,
    }));
    setFiles((prevFiles) => [...prevFiles, ...newFiles]);
  };

  const getFileIcon = (fileType) => {
    if (fileType.includes("pdf")) return "üìÑ"; // PDF icon
    if (fileType.includes("word") || fileType.includes("doc")) return "üìù"; // Word icon
    if (fileType.includes("image")) return "üñºÔ∏è"; // Image icon
    return "üìÅ"; // Default file icon
  };

  return (
    <Box>
      {/* File Upload Input */}
      <input
        accept="*/*"
        style={{ display: "none" }}
        id="file-upload"
        type="file"
        onChange={handleFileUpload}
        multiple // Allow multiple file uploads
      />
      <label htmlFor="file-upload" style={{ marginLeft: "92%" }}>
        <Button variant="contained" color="primary" component="span">
          Upload File
        </Button>
      </label>

      {/* Display Uploaded Files Information */}
      {files.length > 0 && (
        <Paper style={{ marginTop: "16px", padding: "16px" }}>
          <Typography variant="h6" style={{ marginBottom: "16px" }}>
            Uploaded Files
          </Typography>
          {files.map((file, index) => (
            <Box key={index} mb={2} display="flex" alignItems="center">
              {/* Display File Icon */}
              <Box mr={2} style={{ fontSize: "24px" }}>
                {getFileIcon(file.type)}
              </Box>

              {/* File Details */}
              <Typography variant="body1" style={{ marginRight: "8px" }}>
                <strong>File Name:</strong> {file.name}
              </Typography>

              <Typography variant="body1" style={{ marginRight: "8px" }}>
                <strong>File Size:</strong> {`${file.size} bytes`}
              </Typography>

              <Typography variant="body1" style={{ marginRight: "8px" }}>
                <strong>File Type:</strong> {file.type}
              </Typography>

              <Typography variant="body1" style={{ marginRight: "8px" }}>
                <strong>Last Modified:</strong> {new Date(file.lastModified).toLocaleDateString()}
              </Typography>
            </Box>
          ))}
        </Paper>
      )}
    </Box>
  );
};

export default Attachments;
