import React, { useState } from "react";
import { Button, Box, Typography, Table, TableHead, TableBody, TableRow, TableCell, Paper } from "@material-ui/core";

const Attachments = () => {
  const [files, setFiles] = useState([]);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Add the new file to the files array
      setFiles([...files, { name: file.name, size: file.size, type: file.type }]);
    }
  };

  return (
    <Box>
      {/* File Upload Input */}
      <input
        accept="*/*"
        style={{ display: "none" }}
        id="file-upload"
        type="file"
        onChange={handleFileUpload}
      />
      <label htmlFor="file-upload" style={{marginLeft:""}}>
        <Button variant="contained" color="primary" component="span">
          Upload File
        </Button>
      </label>

      {/* Table to display uploaded file information */}
      {files.length > 0 && (
        <Paper style={{ marginTop: "16px" }}>
          <Typography variant="h6" style={{ padding: "16px" }}>
            Uploaded Files
          </Typography>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>File Name</TableCell>
                <TableCell>File Size (bytes)</TableCell>
                <TableCell>File Type</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {files.map((file, index) => (
                <TableRow key={index}>
                  <TableCell>{file.name}</TableCell>
                  <TableCell>{file.size}</TableCell>
                  <TableCell>{file.type}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Paper>
      )}
    </Box>
  );
};

export default Attachments;
