import React, { useEffect, useState } from "react";
import { Button, Box, OutlinedInput } from "@material-ui/core";

const Comments = () => {

    const [open, setOpen] = useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };


    return (
        <><Button
            variant="contained"
            color="primary"
            style={{ marginBottom: "4px", marginLeft: "85%" }}
            onClick={handleClickOpen}
        >
            Comment History
        </Button>
            <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                <Box flexGrow={1} mr={2}>
                    <OutlinedInput
                        placeholder="Add a comment..."
                        multiline
                        rows={3}
                        fullWidth
                        variant="outlined" />
                </Box>
            </Box>




            {/* Dialog for Comment History */}
            <Dialog open={open} onClose={handleClose} maxWidth="sm" fullWidth>
                <DialogTitle>Comment History</DialogTitle>
                <DialogContent>
                    {/* Add your comment history content here */}
                    <Box>Here is where your comment history will be displayed...</Box>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose} color="secondary">
                        Cancel
                    </Button>
                    <Button onClick={() => console.log("Save clicked")} color="primary" variant="contained">
                        Save
                    </Button>
                </DialogActions>
            </Dialog>

        </>
    );

};
export default Comments;
