import React, { useState, useCallback } from "react";
import { Button, Box, Typography, Paper } from "@material-ui/core";
import { FileIcon, defaultStyles } from 'react-file-icon';
import { debounce } from "lodash";

const Attachments = () => {
  const [files, setFiles] = useState([]);

  const handleFileUpload = useCallback(debounce((event) => {
    const file = event.target.files[0];
    if (file) {
      setFiles(prevFiles => [
        ...prevFiles,
        {
          name: file.name,
          size: file.size,
          type: file.type,
          lastModified: file.lastModified,
          lastModifiedDate: file.lastModifiedDate,
        },
      ]);
    }
  }, 300), []); // Debounce for 300ms

  const getFileIcon = (fileType) => {
    // Basic file type to icon mapping (defaultStyles from react-file-icon)
    if (fileType.includes("pdf")) return <FileIcon extension="pdf" {...defaultStyles.pdf} />;
    if (fileType.includes("word") || fileType.includes("doc")) return <FileIcon extension="docx" {...defaultStyles.docx} />;
    if (fileType.includes("excel") || fileType.includes("xls")) return <FileIcon extension="xlsx" {...defaultStyles.xlsx} />;
    if (fileType.includes("image")) return <FileIcon extension="jpg" {...defaultStyles.jpg} />;
    return <FileIcon extension="file" {...defaultStyles.file} />; // Default file icon
  };

  return (
    <Box>
      {/* File Upload Input */}
      <input
        accept="*/*"
        style={{ display: "none" }}
        id="file-upload"
        type="file"
        onChange={handleFileUpload}
      />
      <label htmlFor="file-upload" style={{ marginLeft: "92%" }}>
        <Button variant="contained" color="primary" component="span">
          Upload File
        </Button>
      </label>

      {/* Display Uploaded Files Information */}
      {files.length > 0 && (
        <Paper style={{ marginTop: "16px", padding: "16px" }}>
          <Typography variant="h6" style={{ marginBottom: "16px" }}>
            Uploaded Files
          </Typography>
          {files.map((file, index) => (
            <Box key={index} mb={2} display="flex" alignItems="center">
              {/* Display File Icon */}
              <Box mr={2}>
                {getFileIcon(file.type)}
              </Box>

              {/* File Details */}
              <Typography variant="body1" style={{ marginRight: "8px" }}>
                <strong>File Name:</strong>
              </Typography>
              <Typography variant="body1">{file.name}</Typography>

              <Typography variant="body1" style={{ marginRight: "8px" }}>
                <strong>File Size:</strong>
              </Typography>
              <Typography variant="body1">{`${file.size} bytes`}</Typography>

              <Typography variant="body1" style={{ marginRight: "8px" }}>
                <strong>File Type:</strong>
              </Typography>
              <Typography variant="body1">{file.type}</Typography>

              <Typography variant="body1" style={{ marginRight: "8px" }}>
                <strong>Last Modified:</strong>
              </Typography>
              <Typography variant="body1">{new Date(file.lastModified).toLocaleDateString()}</Typography>
            </Box>
          ))}
        </Paper>
      )}
    </Box>
  );
};

export default Attachments;
