import React, { useState } from "react";
import { Container, Button, Typography, Box } from "@material-ui/core";

const ObjectPage = ({ id, onBack }) => {
  const [activeSection, setActiveSection] = useState("general"); // Track the active section

  // Scroll to the section smoothly
  const scrollToSection = (section) => {
    setActiveSection(section);
    const sectionElement = document.getElementById(section);
    if (sectionElement) {
      sectionElement.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <Container>
      {/* Header with ID and Back button */}
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
        <Typography variant="h5">Reimbursement ID: {id}</Typography>
        <Button variant="contained" color="secondary" onClick={onBack}>
          Back to List
        </Button>
      </Box>

      {/* Navigation Links */}
      <Box mb={2}>
        <Button onClick={() => scrollToSection("general")}>General Information</Button>
        <Button onClick={() => scrollToSection("reimbursement")}>Reimbursement Details</Button>
        <Button onClick={() => scrollToSection("attachments")}>Attachments</Button>
        <Button onClick={() => scrollToSection("workflow")}>Workflow History</Button>
        <Button onClick={() => scrollToSection("comments")}>Comments</Button>
      </Box>

      {/* Sections */}
      <Box id="general" mb={5}>
        <Typography variant="h6">General Information</Typography>
        {/* Add your general information content here */}
        <Typography variant="body1">Details about general information...</Typography>
      </Box>

      <Box id="reimbursement" mb={5}>
        <Typography variant="h6">Reimbursement Details</Typography>
        {/* Add your reimbursement details content here */}
        <Typography variant="body1">Details about reimbursement...</Typography>
      </Box>

      <Box id="attachments" mb={5}>
        <Typography variant="h6">Attachments</Typography>
        {/* Add your attachments content here */}
        <Typography variant="body1">Details about attachments...</Typography>
      </Box>

      <Box id="workflow" mb={5}>
        <Typography variant="h6">Workflow History</Typography>
        {/* Add your workflow history content here */}
        <Typography variant="body1">Details about workflow history...</Typography>
      </Box>

      <Box id="comments" mb={5}>
        <Typography variant="h6">Comments</Typography>
        {/* Add your comments content here */}
        <Typography variant="body1">Details about comments...</Typography>
      </Box>
    </Container>
  );
};

export default ObjectPage;
