import React, { useEffect, useState } from "react";
import { Container, Box, Toolbar, Button, Typography, TextField } from "@mui/material"; // Updated import for Material-UI
import { DataGrid } from "@mui/x-data-grid"; // Updated import for DataGrid
import { getTableData, getTableCount } from "api";

const columns = [
  { field: "reimbursmentId", headerName: "Reimbursment ID", width: 250 },
  { field: "reimbursementDate", headerName: "Reimbursement Date", flex: 1 },
  { field: "totalAmount", headerName: "Total Amount", flex: 1 },
];

const PAGE_SIZE = 15;

export default function MasterPage() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [rowCount, setRowCount] = useState(0);
  const [filterValues, setFilterValues] = useState({
    reimbursmentId: "",
    reimbursementDate: "",
    totalAmount: ""
  });


  const loadData = async (isFirstLoad, skip = 0) => {
    try {
      setLoading(true);
      setItems([]);

      if (isFirstLoad) {
        const count = await getTableCount();
        setRowCount(count);
      }

      const _items = await getTableData({
        $top: PAGE_SIZE,
        $skip: skip,
        ...filterValues // Apply filter values to the request if needed
      });
      const itemsWithIds = _items.map((item, index) => ({
        ...item,
        id: index + skip
      }));
      setItems(itemsWithIds);
    } finally {
      setLoading(false);
    }
  };

  const handleRowClick = (params) => {

  };

  const handlePageChanged = ({ page }) => {
    loadData(false, page * PAGE_SIZE);
  };

  const handleCreate = () => {
    console.log("Create button clicked");
  };

  const handleRefresh = () => {
    loadData(true); // Reload the first page
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilterValues((prev) => ({
      ...prev,
      [name]: value,
    }));
  };


  const handleApplyFilter = () => {
    loadData(true); // Reload data with filters applied
  };

  useEffect(() => {
    loadData(true);
  }, []);

  return (
    <Container disableGutters>
      {/* Filter Toolbar */}
      <Box mb={2}>
        <Toolbar></Toolbar>
        <Toolbar>
          <TextField
            label="Reimbursement ID"
            variant="outlined"
            name="reimbursmentId"
            value={filterValues.reimbursmentId}
            onChange={handleFilterChange}
            style={{ marginRight: "8px" }}
          />
          <TextField
            label="Reimbursement Date"
            variant="outlined"
            name="reimbursementDate"
            type="date"
            value={filterValues.reimbursementDate}
            onChange={handleFilterChange}
            InputLabelProps={{
              shrink: true,
            }}
            style={{ marginRight: "8px" }}
          />
          <TextField
            label="Total Amount"
            variant="outlined"
            name="totalAmount"
            value={filterValues.totalAmount}
            onChange={handleFilterChange}
            style={{ marginRight: "8px" }}
          />
          <Button variant="contained" color="primary" onClick={handleApplyFilter}>
            Apply Filters
          </Button>
        </Toolbar>
      </Box>

      {/* Action Toolbar */}
      <Toolbar>
        <Typography variant="h6" style={{ flex: 1 }}>
          Reimbursement Records
        </Typography>
        <Button variant="contained" color="primary" onClick={handleCreate}>
          Create
        </Button>
        <Button variant="outlined" color="secondary" onClick={handleRefresh} style={{ marginLeft: "8px" }}>
          Refresh
        </Button>
      </Toolbar>

      {/* DataGrid Table */}
      <Box height="80vh" py={5}>
        <DataGrid
          loading={loading}
          rows={items}
          columns={columns}
          pageSize={PAGE_SIZE}
          paginationMode="server"
          rowCount={rowCount}
          onRowClick={handleRowClick} // Add this line for row click handling
          onPageChange={handlePageChanged}
        />
      </Box>
    </Container>
  );
}
