import React, { useEffect, useState } from "react";
import { Button, Box, OutlinedInput, Dialog, DialogActions, DialogContent, DialogTitle, Typography, Divider } from "@material-ui/core";

const Comments = () => {

    const [open, setOpen] = useState(false);
    const [comment, setComment] = useState("");
    const [history, setHistory] = useState([
        { id: 1, text: "Initial comment", date: "2023-10-20", user: "Alice" },
        { id: 2, text: "Follow-up comment", date: "2023-10-25", user: "Bob" },
        // Add more comment history data here as needed
    ]);

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleSaveComment = () => {
        const newComment = {
            id: history.length + 1,
            text: comment,
            date: new Date().toISOString().slice(0, 10), // format date as YYYY-MM-DD
            user: "CurrentUser", // replace with actual user data if available
        };
        setHistory([newComment, ...history]); // add new comment at the beginning
        setComment("");
        setOpen(false);
    };



    return (
        <><Button
            variant="contained"
            color="primary"
            style={{ marginBottom: "4px", marginLeft: "85%" }}
            onClick={handleClickOpen}
        >
            Comment History
        </Button>
            <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                <Box flexGrow={1} mr={2}>
                    <OutlinedInput
                        placeholder="Add a comment..."
                        multiline
                        rows={3}
                        fullWidth
                        variant="outlined" />
                </Box>
            </Box>
            {/* Dialog for Comment History */}
            <Dialog open={open} onClose={handleClose} maxWidth="sm" fullWidth>
                <DialogTitle>Comment History</DialogTitle>

                <DialogContent>
                    {/* Add your comment history content here */}
                    <Box>Here is where your comment history will be displayed...</Box>
                </DialogContent>

                <DialogActions>
                    <Button onClick={() => console.log("Save clicked")} color="primary" variant="contained">
                        Save
                    </Button>
                    <Button onClick={handleClose} color="secondary">
                        Cancel
                    </Button>
                </DialogActions>
            </Dialog>
        </>
    );

};
export default Comments;
